pub fn generate_random_hex_color() -> String {
    let mut rng = rand::thread_rng();
    let r = rng.gen_range(0..255);
    let g = rng.gen_range(0..255);
    let b = rng.gen_range(0..255);

    format!("{:02X}{:02X}{:02X}", r, g, b)
}

